from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port
from pybricks.pupdevices import Motor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub = PrimeHub()
motor_direito = Motor(Port.A, Direction.COUNTERCLOCKWISE)
motor_esquerdo = Motor(Port.B, Direction.CLOCKWISE)
drive_base = DriveBase(motor_esquerdo, motor_direito, 56, 280)

async def main1():
    prime_hub.imu.reset_heading(0)
    motor_direito.reset_angle(0)
    motor_esquerdo.reset_angle(0)
    await drive_base.turn(90)
    print(prime_hub.imu.heading())
    if prime_hub.imu.heading() <= 90:
        await drive_base.turn(prime_hub.imu.heading() - 90)
        print(prime_hub.imu.heading())

async def main2():
    while not True:
        await wait(1)


async def main():
    await multitask(main1(), main2())

run_task(main())