from pybricks.hubs import PrimeHub
from pybricks.parameters import Color, Direction, Port
from pybricks.pupdevices import ColorSensor, Motor, UltrasonicSensor
from pybricks.robotics import DriveBase
from pybricks.tools import multitask, run_task, wait

# Set up all devices.
prime_hub_2 = PrimeHub()
sensor_esquerdo = ColorSensor(Port.C)
sensor_direito = ColorSensor(Port.D)
distance_sensor = UltrasonicSensor(Port.E)
motoresquerdo = Motor(Port.A, Direction.CLOCKWISE)
motordireito = Motor(Port.B, Direction.COUNTERCLOCKWISE)
garra = Motor(Port.F, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(motoresquerdo, motordireito, 56, 110)

# Initialize variables.
erro = 0
correcao = 0
integral = 0
derivada = 0
erro_final = 0
kp = 8
ki = 0.001
kd = 0.01
ref_giroturn = 0
method_stop = 0
power = 0
alinhamento = 0
c_v_ = 0
c_d_ = 0

async def giromovinfiti(powered, ref):
    global method_stop, erro, power
    await wait(1)
    while not (await sensor_esquerdo.color() == Color.NONE and await sensor_direito.color() == Color.NONE or await distance_sensor.distance() <= 70):
        await wait(1)
        method_stop = abs(motoresquerdo.angle() / 360)
        erro = prime_hub_2.imu.heading() - ref
        power = 60
        await PID(kp, ki, kd)
        motordireito.dc(power + correcao)

        motoresquerdo.dc(power - correcao)
        await multitask(wait(1))
    drive_base.stop()

async def PID(kpp, kii, kdd):
    global integral, derivada, correcao, erro_final
    await wait(1)
    integral = integral + erro
    derivada = erro - erro_final
    correcao = erro * kp + (integral * ki + derivada * kd)
    erro_final = erro

async def giro_turn__DIREITO(ref):
    global erro
    await wait(1)
    while not prime_hub_2.imu.heading() >= ref:
        await wait(1)
        erro = prime_hub_2.imu.heading() - ref
        await PID(kp, ki, kd)
        motordireito.dc(1 * correcao)

        motoresquerdo.dc(-1 * correcao)
        await multitask(wait(1))
        print(prime_hub_2.imu.heading())
    drive_base.stop()

async def giro_turn_ESQUERDO(ref):
    global erro
    await wait(1)
    while not prime_hub_2.imu.heading() <= ref:
        await wait(1)
        erro = prime_hub_2.imu.heading() - ref
        await PID(kp, ki, kd)
        motordireito.dc(1 * correcao)

        motoresquerdo.dc(-1 * correcao)
        await multitask(wait(1))
        print(prime_hub_2.imu.heading())
    drive_base.stop()

async def GyroMove(distancia_, powerr, ref):
    global method_stop, integral, erro_final, erro
    await wait(1)
    method_stop = 0
    motordireito.reset_angle(0)

    motoresquerdo.reset_angle(0)
    await multitask(wait(1))
    integral = 0
    erro_final = 0
    while not method_stop > distancia_:
        await wait(1)
        method_stop = abs(motoresquerdo.angle() / 360)
        erro = prime_hub_2.imu.heading() - 0
        await PID(kp, ki, kd)
        motordireito.dc(powerr + alinhamento)

        motoresquerdo.dc(powerr - alinhamento)
        await multitask(wait(1))
    drive_base.stop()

async def main1():
    global alinhamento
    await GyroMove(0.8, 70, 0)
    await giromovinfiti(50, 0)
    for count in range(6):
        await wait(1)
        await GyroMove(0.25, -70, c_d_)
        await giro_turn__DIREITO(c_v_)
        await giromovinfiti(100, c_v_)
        alinhamento = alinhamento + 90
        alinhamento = alinhamento + 90

async def main2():
    pass


async def main():
    await multitask(main1(), main2())

run_task(main())