async def PID(kpp, kii, kdd):
    global integral, derivada, correcao, erro_final
    await wait(1)
    integral = integral + erro
    derivada = erro - erro_final
    correcao = erro * kp + (integral * ki + derivada * kd)
    erro_final = erro

async def seguidor(powerred):
    global erro
    await wait(1)
    while True:
        await wait(1)
        erro = await sensor_direito.reflection() - await sensor_esquerdo.reflection()
        await PID(kp, ki, kp)
        motordireito.dc(powerred + correcao)

        motoresquerdo.dc(powerred - correcao)
        await multitask(wait(1))

