from pybricks.hubs import PrimeHub
from pybricks.parameters import Axis, Direction, Port, Stop
from pybricks.pupdevices import ColorSensor, Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
hub = PrimeHub(top_side=Axis.Z, front_side=Axis.X)
color_sensor = ColorSensor(Port.C)
motor = Motor(Port.A, Direction.CLOCKWISE)
motor_2 = Motor(Port.B, Direction.COUNTERCLOCKWISE)
garra = Motor(Port.E, Direction.COUNTERCLOCKWISE)
drive_base = DriveBase(motor, motor_2, 60, 165)

# Initialize variables.
integral = 0
lasterror = 0
error = 0
derivative = 0
correction = 0
proporcional = 0
int2 = 0
constante_derivada = 0
For_C3_A7a = 0

def gyromove(Degrees, Speed, Ang):
    global integral, lasterror, proporcional, constante_derivada, int2, error
    hub.imu.reset_heading(0)
    motor.reset_angle(0)
    integral = 0
    lasterror = 0
    proporcional = 3
    constante_derivada = 0.2
    int2 = 0.001
    while True:
        error = hub.imu.heading() - Ang
        Calculador_PID(2, 0, 0, 0, 0)
        motor.dc(Speed - correction)
        motor_2.dc(Speed + correction)
        if abs(motor.angle()) < 360 * Degrees:
            break

def line(Degrees, Speed, Ang):
    global integral, lasterror, proporcional, constante_derivada, int2, error
    hub.imu.reset_heading(0)
    motor.reset_angle(0)
    integral = 0
    lasterror = 0
    proporcional = 2
    constante_derivada = 0.2
    int2 = 0.001
    while motor.angle() < 360 * Degrees:
        error = color_sensor.reflection() - 50
        Calculador_PID(2, 0.001, 0.2, 0, 0)
        motor.dc(Speed - correction)
        motor_2.dc(Speed + correction)

def gyroturn(Velocidade, _C3_82ngulo):
    global For_C3_A7a, error
    For_C3_A7a = Velocidade
    hub.imu.reset_heading(0)
    motor.reset_angle(0)
    while True:
        error = hub.imu.heading() - _C3_82ngulo
        Calculador_PID(1, 0.001, 0.02, 0, 0)
        motor.dc(Velocidade - correction)
        motor_2.dc(Velocidade * -1 + correction)
        if abs(error) < 1:
            break
    wait(1000)

def Calculador_PID(Kp, Ki, Kd, SP, SP1):
    global integral, correction, lasterror
    integral = integral + error
    correction = error * proporcional + (integral * int2 + derivative * constante_derivada)
    lasterror = error


# The main program starts here.
garra.run_angle(500, 360)
gyromove(3.42, 60, 35)
gyroturn(-10, -35)
gyromove(4.2, 100, 0)
gyroturn(-65, -90)
garra.run_angle(700, -400)
drive_base.straight(150)
garra.run_angle(700, 400)
gyromove(1.3, -100, 0)
gyroturn(90, 90)
garra.run_angle(700, -400)
drive_base.straight(-75)
drive_base.turn(60)
gyromove(2.5, -100, 0)
drive_base.turn(90)
