from pybricks.hubs import PrimeHub
from pybricks.parameters import Direction, Port, Stop
from pybricks.pupdevices import ColorSensor, Motor
from pybricks.robotics import DriveBase
from pybricks.tools import wait

# Set up all devices.
hub = PrimeHub()
color_sensor = ColorSensor(Port.C)
motor_esquerdo = Motor(Port.A, Direction.CLOCKWISE)
motor_direito = Motor(Port.B, Direction.COUNTERCLOCKWISE)
motor_garra = Motor(Port.E, Direction.COUNTERCLOCKWISE)
robot = DriveBase(motor_esquerdo, motor_direito, 43, 162)

# Initialize variables.
error = 0
integral = 0
last_error = 0
Kp = 0
Ki = 0
Kd = 0
Derivative = 0
correction = 0
Power = 0
Referencial = 0
method_calibrate = 0

def GyroMove(Degrees_Motor, Speed):
    global Kp, Ki, Kd, integral, last_error, Power, Referencial, error
    hub.imu.reset_heading(0)
    motor_esquerdo.reset_angle(0)
    motor_direito.reset_angle(0)
    Kp = 2.3
    Ki = 0.0001
    Kd = 0.1
    integral = 0
    last_error = 0
    Power = Speed
    Referencial = 0
    while abs(motor_esquerdo.angle()) < Degrees_Motor:
        error = hub.imu.heading() - 0
        PID()
        motor_esquerdo.dc(Power - correction)
        motor_direito.dc(Power + correction)
    motor_esquerdo.stop()
    motor_direito.stop()
    wait(500)

def PID():
    global integral, Derivative, correction
    integral = error
    Derivative = error - last_error
    correction = error * Kp + (integral + Ki) * (Derivative * Kd)

def Abrir_Garra(Rota_C3_A7_C3_A3o, Velocidade):
    motor_garra.run_angle(Velocidade, Rota_C3_A7_C3_A3o)

def GyroTurn(Refer_C3_AAncia, Speed):
    global Kp, Ki, Kd, integral, last_error, Power, Referencial, error
    hub.imu.reset_heading(0)
    motor_esquerdo.reset_angle(0)
    motor_direito.reset_angle(0)
    Kp = 2
    Ki = 0.001
    Kd = 0.2
    integral = 0
    last_error = 0
    Power = Speed
    Referencial = Refer_C3_AAncia
    while abs(hub.imu.heading()) < abs(Referencial):
        error = hub.imu.heading() - Referencial
        PID()
        if Referencial > 0:
            motor_esquerdo.dc(Power + correction)
            motor_direito.dc(Power * -1 - correction)
        else:
            motor_esquerdo.dc(Power * -1 - correction)
            motor_direito.dc(Power + correction)
    motor_esquerdo.stop()
    motor_direito.stop()
    wait(500)


# The main program starts here.
Abrir_Garra(650, 800)
GyroMove(530, 100)
GyroTurn(-86, 80)
GyroMove(2060, 100)
GyroTurn(-89, 80)
Abrir_Garra(-650, 800)
GyroMove(290, 100)
Abrir_Garra(650, 800)
GyroMove(580, -100)
GyroTurn(90, 80)
GyroMove(90, 100)
Abrir_Garra(-650, 800)
method_calibrate = hub.imu.heading()
GyroMove(150, -100)
GyroTurn(30 - method_calibrate, 80)
GyroMove(750, -100)
GyroTurn(60, 80)
GyroMove(3000, 100)
